INSERT INTO `a_collection` VALUES ('1000000358', '20151101000000', 'Nike Air Flightposite 1 风一 GU 球鞋', 'Nike Air Flightposite 1 风一 GU 球鞋', '篮球鞋即SNEAKER，同时也将球鞋爱好者融入到SNEAKER一词中，使得SNEAKER有更宽泛的定义。 篮球是一项剧烈运动，为了能应付激烈的运动，对于一双篮球鞋来讲，就需要有很好的耐久性，支撑性、稳定性，舒适性和良好的减震作用。', '0', '0', '1000000042', '低调的一哥', '/m_image/2017/11/07/20171107002431893.jpg', null, '非卖品', '20171107002542', 'N', '1', '2', '1', null, '-45856378');
INSERT INTO `a_collection` VALUES ('1000000359', '20171101000000', 'Nike KIDD 贾森基德 GU 球鞋', 'Nike KIDD 贾森基德 GU 球鞋', '篮球运动中不断的起动、急停、起跳和迅速的左右移动等动作使得你在挑选篮球鞋时必须把鞋的特性放在绝对首位。另外，你个人的打球风格也是一个很重要的因素，可以根据这个选择自己所需的不同类型的篮球鞋。', '-1', '0', '1000000042', '低调的一哥', '/m_image/2017/11/07/20171107002742557.jpg', null, '19999', '20171107002922', 'N', '1', '2', '1', null, '-415938180');
