INSERT INTO `a_collection` VALUES ('1000000366', '20171101000000', 'Nike 风一 黑橙 GU', 'Nike 风一 黑橙 GU', '虽然中国制鞋业如今也面临着国内政策因素和劳动力成本上涨影响以及来自印度、巴西、越南、印度尼西亚等国家在中低档鞋类方面的竞争，在高档鞋方面又有意大利、西班牙、葡萄牙等国家的竞争，但中国制鞋业的综合竞争优势仍然是其它国家难以匹比的。', '0', '0', '1000000042', '低调的一哥', '/m_image/2017/11/07/20171107005418060.jpg', null, '非卖品', '20171107005723', 'N', '1', '3', '1', null, '-344557323');
