INSERT INTO `a_collection` VALUES ('1000000389', '20100101000000', 'Nike 私人定制 Nike Air Flightposite 1', 'Nike 私人定制 Nike Air Flightposite 1', 'AR、NIKE的AIR FORCE 1、ADIDAS的SUPER STAR等等，这些运动鞋将其本身单调的运动机械化逐步引导走向舒适、休闲、时尚化，真正树立起属于SNEAKER的文化。\r\n而将SNEAKER文化推至极致的，是属于1985年的AIR JORDAN系列篮球鞋。AIR JORDAN系列归属于NIKE旗下，是为被誉为篮球之神的MICHEAL JORDAN而量身定做的一个系列。随着JORDAN进入NBA大红大紫，AIR JORDAN系列篮球鞋也被大众所喜爱，SNEAKER的文化也就此升值极致。', '0', '0', '1000000042', '低调的一哥', '/m_image/2017/11/07/20171107113135247.jpg', null, '非卖品', '20171107113615', 'N', '2', '2', '1', null, '-114370953');
