INSERT INTO `a_collection` VALUES ('1000000405', '20171101000000', 'be@rbrick 联名款 稀有潮流玩具', 'be@rbrick 联名款 稀有潮流玩具', '原则上暴力熊并不是指Bearbrick，而是血粉熊，英文名字叫做Gloomy，出自于大阪插画家Mori Chack之手。至于为什么国内会有关于各种熊概念的混淆，甚至把类似的几种熊的形象统称为暴力熊，应该是由于在这些形象和玩具最初进入中国时，并不是通过官方渠道和正规代理商进入，属于民间流传。而各个熊也因此没有正规的中文名。各界群众为了更好地称呼熊们，便急迫地需要一个上口的中文名字，于是暴力熊的名字统称了各个种类的潮流熊形象，造成以后也无法再进行明确地区分，最终暴力熊的名字流传开来，很多人把Bearbrick错称为暴力熊。', '0', '0', '1000000044', 'batman0811', '/m_image/2017/11/07/20171107133757360.jpg', null, '非卖品', '20171107133858', 'N', '1', '2', '1', null, '-530326706');
