INSERT INTO `a_collection` VALUES ('1000000449', '20171101000000', '木质 联名款 Be@rbrick 稀有潮流模', '木质 联名款 Be@rbrick 稀有潮流模', '版本：所谓玩家们经常说“日版”，“港版”等，其实只是售卖地的区别。在玩具本身上无任何区别。都是同一厂家生产。只不过会由于售卖地的不同，相关法律不同，可能会加贴不同认证标识。其他就完全一样了。能够买到的大陆版也是和日本完全一样的。', '0', '0', '1000000044', 'batman0811', '/m_image/2017/11/07/20171107144458438.jpg', null, '非卖品', '20171107144604', 'N', '1', '2', '1', null, '-337322245');
