INSERT INTO `a_collection` VALUES ('1000000546', '20171101000000', '艾弗森 球星卡', '艾弗森 球星卡', '2000-2001年度赛季，布朗教练决定身高只有六尺的艾弗森改打得分后卫，结果76人于本季取得了开季十连胜，而季尾亦以东部头种子的姿态进入季后赛。而艾弗森于本季更是取得了巨大的成功，以场均31.1分、3.8个篮版、4.6次助攻、2.5次抢断及0.3个盖帽的成绩拿下联盟MVP，并同时夺得得分王，抢断王，及再度入选联盟第一队。', '0', '0', '1000000040', '花花牛', '/m_image/2017/11/07/20171107163523453.jpg', null, '非卖品', '20171107163527', 'N', '1', '2', '1', null, '-750053652');
