INSERT INTO `a_collection` VALUES ('1000000554', '20171101000000', '艾弗森 球星卡', '艾弗森 球星卡', '2011年1月12日，由于艾弗森在土耳其联赛中小腿受伤，他准备离开土耳其返回美国进行手术，需要6至8周的休养时间。最终，艾弗森选择了返回美国做手术，并退出篮坛。同时，也就结束了在土耳其的职业生涯。', '0', '0', '1000000040', '花花牛', '/m_image/2017/11/07/20171107164001703.jpg', null, '非卖品', '20171107164008', 'N', '2', '2', '1', null, '-778716757');
