INSERT INTO `a_collection` VALUES ('1000000743', '19910101000000', 'Goro\'s 印第安风格银饰', 'Goro\'s 印第安风格银饰', '高桥吾郎1939年出生于东京。在高中的时候，从驻日美军那里习得皮革雕刻的技术并自学，27岁时在东京青山开了一间小店。喜欢印地安与美国文化的他，喜欢皮革制品，喜欢制作银饰，后来又到了美国某个印地安部落生活了多年。在那里，他有了属于自己的印地安名字，成为了该部落的勇士，并学会了该部落特有的饰品制作方式。', '0', '0', '1000000041', '高大桥', '/m_image/2017/11/07/20171107233526644.jpg', null, '非卖品', '20171107233619', 'N', '1', '2', '1', null, '-800103346');
