INSERT INTO `a_collection` VALUES ('1000000747', '20171101000000', 'Goro\'s 印第安风格银饰', 'Goro\'s 印第安风格银饰', '自60年代创立以来，其精致的制作手艺，神秘的古印第安传统技术、独特的款式设计，令其建立于日本东京唯一的发售店GORO\'S每天都吸引了来自全国各地的FANS排长队等待进入他的专卖店。[1]  GORO\'S这个品牌已经有40余年历史了，不做任何广告，坚持着纯手工，少量生产，让他成为与CHROME HEARTS 并列世界奢侈品行列的两个银饰品牌。创办人兼设计师——高桥吾郎，是日本流行界的一个传奇人物。', '0', '0', '1000000041', '高大桥', '/m_image/2017/11/07/20171107233739912.jpg', null, '非卖品', '20171107234028', 'N', '1', '3', '1', null, '-64072610');
