INSERT INTO `a_collection` VALUES ('1000000762', '20171101000000', 'Goro\'s 印第安风格银饰', 'Goro\'s 印第安风格银饰', '沿着印第安技术所制作的GORO’S，其出品的魅力所在之处，在于全部围绕着古印第安民族的经典造型所设计，这种差不多绝迹世界的手工艺技术出品，今回传授到银器职人高桥吾郎手中，更混合了日本民族坚持制作高水平作品的优点，成功化为了代表着日本时尚界的孤高名作更影响着以后UNDERCOVER，JANHOME MADE等时尚日牌的饰物发展。', '0', '0', '1000000041', '高大桥', '/m_image/2017/11/07/20171107235511146.jpg', null, '非卖品', '20171107235604', 'N', '1', '2', '1', null, '-177140344');
INSERT INTO `a_collection` VALUES ('1000000763', '20171101000000', 'Goro\'s 印第安风格银饰', 'Goro\'s 印第安风格银饰', 'GORO’S的作品往往总给人一种古着饰物的感觉，惯常使用皮革连系的各式各样ACCESSORIES单品，利用鹰爪、羽毛、太阳、盾牌……等印第安色彩元素的图像制作，更给人一种神秘的魅力，当中部分的尊贵出品更混合了18K镀金的点缀，相比起传统的金银饰物更显时尚的味道。', '0', '0', '1000000041', '高大桥', '/m_image/2017/11/07/20171107235649056.jpg', null, '非卖品', '20171107235711', 'N', '1', '1', '1', null, '-521075345');
